mapper.update:
update student
set name = #{name,jdbcType=VARCHAR},
  age = #{age,jdbcType=INTEGER},
  teacher_id = #{teacherId,jdbcType=INTEGER},
  school_id = #{schoolId,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, name, age, teacher_id, school_id
from student
where id = #{id,jdbcType=INTEGER}

